<?phpif(empty($_mb_dir_)) { exit('sorry'); }if(!function_exists('magicbox_fixDate')) {function magicbox_fixDate($format, $originalDate, $hesapla = ""){    if ($hesapla != "") {        return date($format, strtotime($hesapla, strtotime($originalDate)));    } else {        if (is_numeric($originalDate)) {            return date($format, $originalDate);        }        return date($format, strtotime($originalDate));    }}}if(!function_exists('magicbox_timeElapsed')) {function magicbox_timeElapsed($datetime, $full = false) {    $now = new DateTime;    $ago = new DateTime($datetime);    $diff = $now->diff($ago);    $diff->w = floor($diff->d / 7);    $diff->d -= $diff->w * 7;    $string = array(        'y' => 'year',        'm' => 'month',        'w' => 'week',        'd' => 'day',        'h' => 'hour',        'i' => 'minute',        's' => 'second',    );    foreach ($string as $k => &$v) {        if ($diff->$k) {            $v = $diff->$k . ' ' . $v . ($diff->$k > 1 ? 's' : '');        } else {            unset($string[$k]);        }    }    if (!$full) $string = array_slice($string, 0, 1);    return $string ? implode(', ', $string) . ' ago' : 'just now';}}?>