<?php  class Magicbox_CacheApc{    private static $instance = NULL;    private $lifeTime = 43200;    protected function __construct($args)    {    return $this;    }    /* Singleton Pattern */    public static function  getInstance($args = array())    {        if (self::$instance == NULL) {            return self::$instance[md5(serialize($args))] = new Magicbox_CacheApc($args);        }        return self::$instance[md5(serialize($args))];    }    function flush()    {        apc_clear_cache();        apc_clear_cache('user');        apc_clear_cache('opcode');    }    public function get($key)    {        return apc_fetch($key);    }    public function set($key, $data)    {        return apc_store(            $key,            $data,            $this->lifeTime        );    }    public function delete($key)    {        return apc_delete($key);    }}?>